
public class Ejercicio6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 4;
		float b = 13.3F;
		boolean c = false;
		a += 2;
		System.out.println(a);
		a -= 3;
		System.out.println(a);
		a *= 3;
		System.out.println(a);
		a /= 2;
		System.out.println(a);
		a %= 4;
		System.out.println(a);
		b /= 2;
		System.out.println(b);
		c &= true;
		System.out.println(c);
		c |= true;
		System.out.println(c);


	}

}
